// SPDX-License-Identifier: MIT
pragma solidity ^0.7;

import "./vendor/token/ERC20/ERC20Burnable.sol";
import "./vendor/access/AccessControl.sol";
import "./vendor/access/Ownable.sol";

contract Token is ERC20Burnable, AccessControl, Ownable {
    
    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
    bytes32 public constant BURNER_ROLE = keccak256("BURNER_ROLE");
    
    constructor (string memory name, string memory symbol, uint256 initialMint) ERC20(name, symbol) {
        _mint(_msgSender(), initialMint * (10 ** uint256(decimals())));
        _setupRole(MINTER_ROLE, _msgSender());
        _setupRole(BURNER_ROLE, _msgSender());
        _setupRole(DEFAULT_ADMIN_ROLE, _msgSender());
    }
    
    function mint(address to, uint256 amount) public {
        require(hasRole(MINTER_ROLE, _msgSender()), "Caller is not a minter");
        _mint(to, amount);
    }
    
    function burn(uint256 amount) public override {
        require(hasRole(BURNER_ROLE, _msgSender()), "Caller is not a burner");
        super.burn(amount);
    }
    
    function burnFrom(address account, uint256 amount) public override {
        require(hasRole(BURNER_ROLE, _msgSender()), "Caller is not a burner");
        super.burnFrom(account, amount);
    }
    
}
